import React, { Component } from 'react';
import axios from 'axios';


export default class Messagerie extends Component {
    state = { messages: [] };
    static contextType = AuthContext;

    handleChange = (evt) => {
        this.setState({ [evt.target.name]: evt.target.value });
    };

    handleSubmit = async (evt) => {
        evt.preventDefault();

        const { writer,
            reader,
            id_annonce,
            message_content,
         } = this.state;

        await axios.post("http://localhost:5555/messagerie", {
            writer: this.context.currentUser._id,
            reader,
            id_annonce,
            message_content,
            date: Date.now(),
        });
    };

    render() {



        return (
            <div className="messagerie">
                <h1>Messagerie</h1>
                <label htmlFor="price" className="label">saisir message</label>
                <input type="text" />
                <textarea name="sd" cols="39" rows="10"></textarea>

            </div>
        )
    }
}
