import React, { Component } from 'react';
import AuthContext from "./../auth/AuthContext";
import axios from "axios";


export default class ManageAnnounce extends Component {

    state = {
        categorie: [],
        annonces: []
    }
    handleChange = (evt) => {
        this.setState({ [evt.target.name]: evt.target.value });
    };
    static contextType = AuthContext;

    async componentDidMount() {
        const categories = await axios.get("http://localhost:5555/categories")
        this.setState({ categorie: categories.data })
        const allAnnonces = await axios.get("http://localhost:5555/annonce/")
        this.setState({ annonces: allAnnonces.data })
        console.log("get annonce",allAnnonces);
        console.log("get annonce data ",allAnnonces.data);


        // test avec map 
        const annoncemap = allAnnonces.data.map((annoncemap, i)=>{
            return annoncemap
        })
        console.log("Map sur annonce", annoncemap);
        const annoncemapdata = annoncemap[0]._id;

        console.log("ya quoi dans la data",annoncemapdata);

        // test avec filter
        function annonces(obj){
            if (obj._id == !undefined) 
            {return true} 
            else {return console.error("message erreur");}
        };
        var annonceID=allAnnonces.data.filter(annonces);

        console.log("annonceid",annonceID);
        console.log("this context",this.context);
        const idUserConnected = this.context.currentUser._id;
        console.log("ID de l'user connecté dans le contexte",this.context.currentUser._id);
    }


    render() {
        const annonces = this.state.annonces;
        console.log("annonces",annonces);

        const categorie = this.state.categorie
        // console.log("@@@@@@@", categorie)

        return (
            <div className="manage-announce">
                <h1> Manage annonce </h1>


                <form className="form"
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}>
                    <br />
                    <label htmlFor="title" className="label"></label>
                    <input type="text" name="title" className="input" placeholder="titre de l'annonce" />
                    <br />
                    <label htmlFor="description" className="label"></label>
                    <input type="text" name="description" className="input" placeholder="description de l'annonce" />
                    <br />


                    <label htmlFor="category_name" className="label"> Catégorie : </label>
                    <select name="category_name" id="category_name">
                        <option>choisir</option>
                        {categorie.map((categ, i) => {
                            return <option value={categ.category_name} key={i}> {categ.category_name} </option>
                        })}
                    </select>
                    <br />
                    <label htmlFor="active" className="active"> Annonce active </label>
                    <select name="active" id="active">
                        <option>Choisir</option>
                        <option value="true">active</option>
                        <option value="false">inactive</option>
                    </select>
                    <br />
                    <button className="btn">create !!!</button>
                </form>
            </div>
        )
    }
}


