import React, { Component } from 'react';
import axios from 'axios';
import AuthContext from "./../auth/AuthContext"


export default class Messagerie extends Component {
    state = { messages: [] };
    static contextType = AuthContext;

    handleChange = (evt) => {
        this.setState({ [evt.target.name]: evt.target.value });
    };

    handleSubmit = async (evt) => {
        evt.preventDefault();

        const { writer,
            reader,
            id_annonce,
            message_content,
        } = this.state;

        await axios.post(process.env.REACT_APP_BACKEND_URL+ '/messagerie', {
            writer: this.context.currentUser._id,
            reader,
            id_annonce,
            message_content,
            date: Date.now(),
        });
    };


    async componentDidMount() {
        const annonces = await axios.get(process.env.REACT_APP_BACKEND_URL+ '/annonce')
        this.setState({ annonces: annonces.data })
    }

    render() {
        const { annonces } = this.state;

        return (
            <div className="messagerie">
                <h1>Messagerie</h1>
                <label htmlFor="price" className="label">saisir message</label>
                <input type="text" />
                <textarea name="sd" cols="39" rows="10">
                    <p>TEST</p>
                </textarea>
            </div>
        )
    }
}
