import React, { Component } from 'react';
import axios from 'axios'


export default class EditProfile extends Component {

    state = {
        first_name: "",
        last_name: '',
        email: '',
        adress: {},
        gender: '',
        age: '',
        Profile_type: '',
        password: '',
        description: ''
    }

    async componentDidMount() {
        await axios.get('http://localhost:5555/user/5f4e11e28a2f53666d6fa0f4')
            .then(response => {
                console.log(response, "vous etes connecte");
                this.setState({
                    first_name: response.data.first_name,
                    last_name: response.data.last_name,
                    email: response.data.email,
                    adress: response.data.adress,
                    gender: response.data.gender,
                    age: response.data.age,
                    profile_type: response.data.profile_type,
                    password: response.data.password,
                    description: response.data.description,
                })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    onChangeFirstName(e) {
        this.setState({
            first_name: e.target.value
        });
    }

    onChangeLastName(e) {
        this.setState({
            last_name: e.target.value
        });
    }
    onChangeEmail(e) {
        this.setState({
            email: e.target.value
        });
    }
    onChangeAdress(e) {
        this.setState({
            adress: e.target.value
        });
    }
    onChangeGender(e) {
        this.setState({
            gender: e.target.value
        });
    }
    onChangeAge(e) {
        this.setState({
            age: e.target.value
        });
    }
    onChangeProfilType(e) {
        this.setState({
            Profile_type: e.target.value
        });
    }
    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }
    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }


    onSubmit(e) {
        e.preventDefault();

        const user = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            address: this.state.address,
            gender: this.state.gender,
            age: this.state.age,
            Profile_type: this.state.Profile_type,
            password: this.state.password,
            description: this.state.description

        }
        console.log(user);
        axios.post('http://localhost:5555/user/editprofile/5f4e11e28a2f53666d6fa0f4')
            .then(res => console.log(res.data));

    }


    render() {
        return (
            <div className="edit-profile">
                <h1>EditProfile</h1>

                <form onSubmit={this.onSubmit}>
                    <label> first name</label>
                    <select value={this.state.first_name}
                        onChange={this.onChangeFirstName}>
                    </select>

                    <br />
                    <label htmlFor="email" className="label">Adresse Mail : </label>
                    <input id="email" name="email" type="email" className="input" placeholder={this.state.email} />


                    {this.state.first_name}
                    {this.state.last_name}

                    {this.state.address
                    }{this.state.gender}
                    {this.state.age}
                    {this.state.Profile_type}
                    {this.state.password}
                    {this.state.description}

                </form>

            </div>
        )
    }
}
