import React, { Component } from 'react';
// import axios from 'axios';
import { APIHandler } from './../../api/handler';
const productHandler = new APIHandler("/categories");


export default class CategorieAdmin extends Component {
    state = { categories: [] }


    // async componentDidMount() {
    //     const allCateg = await axios.get("http://localhost:5555/categories")
    //     this.setState({ categories: allCateg.data })
    // };
    async componentDidMount() {
        const apiRes = await productHandler.getAll();
        this.setState({ categories: apiRes.data });
      }

    handleDelete = async (id) => {
        console.log("@@@@",this.state.categs)
       const hamidou=  await productHandler.deleteOne(id);
        console.log("@@@@@",hamidou);
        const apiRes2 = await productHandler.getAll();
        console.log(apiRes2);
        this.setState({ categs: apiRes2.data })
    }

    render() {

        const categ = this.state.categories;
        return (
            <div className="categorie-admin">
                <h1>Categories Admin</h1>
                {/* { this.state[0].first_name  !== undefined ? this.state[0].first_name : ""} */}
                <table className="product-manage-table">
                    <thead>
                        <tr className="table-row">
                            <th className="table-head">Name</th>
                            <th className="table-head">Edit</th>
                            <th className="table-head">Delete </th>
                        </tr>
                    </thead>
                    <tbody>
                        {categ.map((cat, i) => {
                            return <tr key={i} className="table-row">
                                <td className="table-div" > {cat.category_name}  </td>
                                <td className="table-div"> <button className="button muted-button">Edit</button> </td>
                                <td className="table-div"> <button className="button muted-button" 

                                onClick={() => this.handleDelete(this.state.categs)}

                                >Delete</button> </td>
                            </tr>
                        })}
                    </tbody>
                </table>
            </div>
        )
    }
}
