import React, { Component } from 'react';
import { Link } from "react-router-dom";
import AuthContext from "./../components/auth/AuthContext";

state = {
    // définition de valeurs de base pour la phase de dev ("mettre à chaîne vide une fois dev ok")
    email: "",
    password: "",
  };


export default class LoginPageSignIn extends Component {
    render() {
        return (
            <div className="LoginPage">
                <h1>Signin</h1>
                <Link to="/signup">S'inscrire.</Link>
                <form action="/signin"></form>
                <form className="form">
                    <h1 className="title">connexion</h1>
                    <label htmlFor="price" className="label">Adresse Mail : </label>
                    <input  name="email" type="email" className="input" required autoComplete="username"/>
                    <br />
                    <label htmlFor="price" className="label">Mot de passe : </label>
                    <input name="password" type="password" className="input" minLength="6" required autoComplete="current-password" />
                    <br />
                    <button className="btn">Se connecter</button>
                </form>


            </div>

        )
    }
}
