import React, { Component } from 'react';
import { APIHandler } from './../../api/handler';
const apiHandler = new APIHandler("/categories");





export default class AddCateg extends Component {
    handleChange = (evt) => {
        this.setState({ [evt.target.name]: evt.target.value });
    };

    handleSubmit = async (evt) => {
        evt.preventDefault();
        const apiRes = await apiHandler.createOne(this.state);
        console.log(apiRes);
    };

    render() {
        return (
            <div className="add-categ">
                <h1> create category</h1>

                <form
                    className="form-create-product"
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}
                >
                    <br />
                    <label htmlFor="category_name" className="label"></label>
                    <input type="text" name="category_name" className="input" placeholder="Nom de la catégorie" />
                    <br />
                    <label htmlFor="description" className="label"></label>
                    <input type="text" name="description" className="input" placeholder="description de l'annonce" />
                    <br />


                    <label htmlFor="category-name" className="label"> Catégorie : </label>

                    <br />
                    <label htmlFor="active" className="active"> Annonce active </label>
                    <select name="active" id="active">
                        <option value="true">active</option>
                        <option value="false">inactive</option>
                    </select>
                    <br />
                    <button className="btn">create !!!</button>
                </form>
            </div>
        )
    }
}
