import React, { Component } from 'react';
import axios from 'axios';


export default class CreateAnnonce extends Component {
    state = {};
    handleChange = (evt) => {
        this.setState({ [evt.target.name]: evt.target.value });
    };

    handleSubmit = async (evt) => {
        evt.preventDefault();


        const adress = {
            district: this.state.adress_area,
            street_number: this.state.adress_number,
            street: this.state.adress_street
        }
        const gender = this.state.gender;
        const age = this.state.age;
        const Profile_type = this.state.Profile_type;
        const email = this.state.email;
        const password = this.state.password;
        const first_name = this.state.first_name;
        const last_name = this.state.last_name;
        // const avatar = this.state.avatar;
        const description = this.state.description;



        await axios.post("http://localhost:5555/user", { adress, email, age, password, first_name, last_name, description, gender, Profile_type });
    };

    render() {
        return (
            <div className="create-annonce">
                <h1>Cr√©er une annonce </h1>
                <form className="form"
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}>

                    <br />
                    <label htmlFor="title" className="label"></label>
                    <input type="text" name="title" type="text" className="input" placeholder="titre de l'annonce" />
                    <br />
                    <label htmlFor="description" className="label"></label>
                    <input type="text" name="description" type="text" className="input" placeholder="description de l'annonce" />
                    <button className="btn">create !!!</button>
                </form>
            </div>
        )
    }
}
